 import random

def generate():
    test_cases = []
    for i in range(10):
        a = random.randrange(1, 10)
        b = random.randrange(1, 10)
      test_cases.append("{} {}".format(a, b))
    return test_cases

def solve(dataset):
    a, b = dataset.split()
    a, b = int(a), int(b)
    return str(a + b)

def check(reply, clue):
    if int(reply) == int(clue):
        return 1
    return 0
